hinge_pin_radius = 1.5;

// Colors
rail_color = [0.7, 0.7, 0.7];
rung_color = [0.6, 0.6, 0.6];
grip_color = [0.2, 0.2, 0.2];
hinge_color = [0.5, 0.5, 0.5];

module ladder() {
    // Left rail
    color(rail_color) {
        translate([-ladder_width/2, 0, 0])
            cube([rail_thickness, ladder_height, rail_thickness]);

        // Rail cap
        translate([-ladder_width/2, -rail_thickness/2, ladder_height])
            cube([rail_thickness, rail_thickness*2, rail_thickness]);
    }

    // Right rail
    color(rail_color) {
        translate([ladder_width/2 - rail_thickness, 0, 0])
            cube([rail_thickness, ladder_height, rail_thickness]);

        // Rail cap
        translate([ladder_width/2 - rail_thickness, -rail_thickness/2, ladder_height])
            cube([rail_thickness, rail_thickness*2, rail_thickness]);
    }

    // Rungs
    for (i = [0:rung_count-1]) {
        color(rung_color) {
            translate([-ladder_width/2 + rail_thickness, 
                     i*rung_spacing + rung_spacing, 
                     rail_thickness/2])
                cube([ladder_width - rail_thickness*2, rung_thickness, rung_thickness]);
        }

        // Anti-slip texture on rungs
        color(grip_color) {
            for (j = [0:3]) {
                translate([-ladder_width/2 + rail_thickness + 2 + j*5, 
                         i*rung_spacing + rung_spacing + rung_thickness/2 - 0.5, 
                         rail_thickness/2 + rung_thickness])
                    cube([1, 1, 0.5]);
            }
        }
    }

    // Bottom feet with non-slip pads
    color(grip_color) {
        // Left foot
        translate([-ladder_width/2 - 1, -1, -feet_height])
            cube([rail_thickness + 2, rail_thickness + 2, feet_height]);

        // Right foot
        translate([ladder_width/2 - rail_thickness - 1, -1, -feet_height])
            cube([rail_thickness + 2, rail_thickness + 2, feet_height]);
    }

    // Hinge mechanism for folding
    color(hinge_color) {
        // Left hinge half
        translate([-ladder_width/2, -rail_thickness, ladder_height/2])
            rotate([0, 90, 0])
                cylinder(h=rail_thickness, r=hinge_pin_radius, $fn=8);

        // Right hinge half
        translate([ladder_width/2 - rail_thickness, -rail_thickness, ladder_height/2])
            rotate([0, 90, 0])
                cylinder(h=rail_thickness, r=hinge_pin_radius, $fn=8);

        // Hinge pin
        translate([-ladder_width/2 + rail_thickness/2, -rail_thickness, ladder_height/2])
            rotate([0, 90, 0])
                cylinder(h=ladder_width - rail_thickness, r=hinge_pin_radius*0.7, $fn=8);
    }

    // Support braces
    color(rail_color) {
        translate([-ladder_width/2 + rail_thickness, -rail_thickness/2, 0])
            cube([ladder_width - rail_thickness*2, rail_thickness, rail_thickness]);
    }

    // Safety warnings
    color([1, 0.2, 0.2]) {
        translate([-ladder_width/2 + rail_thickness + 1, 5, rail_thickness + 0.1])
            cube([ladder_width - rail_thickness*2 - 2, 2, 0.2]);
    }
}

