// Colors
step_color = "#E0E0E0";
leg_color = "#404040";
grip_color = "#202020";

// Main step platform with rounded edges
module step_platform() {
    color(step_color) {
        // Main platform
        translate([0, 0, step_height/2]) {
            difference() {
                // Main block
                cube([stool_width, stool_depth, step_height], center=true);

                // Hollow interior
                cube([stool_width - 2*wall_thickness, 
                      stool_depth - 2*wall_thickness, 
                      step_height - wall_thickness], center=true);
            }
        }

        // Rounded top edges using cylinders
        for(i = [-1, 1]) {
            for(j = [-1, 1]) {
                translate([i*(stool_width/2 - wall_thickness), 
                          j*(stool_depth/2 - wall_thickness), 
                          step_height - wall_thickness/2]) {
                    cylinder(h=wall_thickness, r=wall_thickness, center=true);
                }
            }
        }
    }
}

// Anti-slip grips on top surface
module anti_slip_grips() {
    color(grip_color) {
        // Horizontal grips
        for(x = [-30: grip_spacing: 30]) {
            translate([x, 0, step_height + 0.5]) {
                cube([grip_radius*2, stool_depth - 10, grip_radius], center=true);
            }
        }
    }
}

// Support leg with rounded base
module support_leg(x_pos, y_pos) {
    color(leg_color) {
        // Vertical support
        translate([x_pos, y_pos, leg_height/2]) {
            difference() {
                cylinder(h=leg_height, r=wall_thickness*2, center=true);
                cylinder(h=leg_height + 1, r=wall_thickness*2 - wall_thickness, center=true);
            }
        }

        // Rounded base pad
        translate([x_pos, y_pos, -1]) {
            cylinder(h=3, r=wall_thickness*2.5, $fn=20);
        }
    }
}

// Reinforcement rib between legs
module reinforcement_rib(x1, y1, x2, y2, height) {
    color(leg_color) {
        translate([(x1+x2)/2, (y1+y2)/2, height/2]) {
            rotate([0, 0, atan2(y2-y1, x2-x1)]) {
                cube([sqrt(pow(x2-x1, 2) + pow(y2-y1, 2)), wall_thickness, height], center=true);
            }
        }
    }
}

