module platform_base() {
    // Main platform body
    difference() {
        // Base block
        translate([0, 0, platform_h/2])
            cube([platform_w, platform_l, platform_h], center=true);

        // Grip slots on sides
        for (i = [-1, 1]) {
            translate([i * (platform_w/2 - 2), 0, platform_h/2])
                cube([4, platform_l - 10, platform_h], center=true);
        }

        // Center hole for tools
        translate([0, 0, -1])
            cylinder(h=platform_h+2, r=6, $fn=32);
    }

    // Raised edges for part retention
    for (edge = [-1, 1]) {
        translate([0, edge * (platform_l/2 - 2), platform_h - 1])
            cube([platform_w - 4, 2, 2], center=true);
    }

    // Grip texture on top surface
    for (x = [-15:5:15]) {
        for (y = [-30:5:30]) {
            translate([x, y, platform_h + 0.1])
                cube([1, 1, 0.5], center=true);
        }
    }
}

module handle() {
    // Main handle shaft
    rotate([90, 0, 0])
        cylinder(h=handle_len, r=handle_d/2, center=true, $fn=32);

    // Handle grip
    translate([0, handle_len/2 + 5, 0])
        rotate([90, 0, 0])
            cylinder(h=15, r=handle_d/2 + 2, center=true, $fn=32);

    // Grip rings
    for (i = [0:2:12]) {
        translate([0, handle_len/2 - i, 0])
            rotate([90, 0, 0])
                cylinder(h=1, r=handle_d/2 + 2.5, center=true, $fn=32);
    }

    // Connection bracket
    translate([0, -handle_len/2 - 5, 0])
        rotate([90, 0, 0])
            cylinder(h=10, r=handle_d/2 + 1, center=true, $fn=32);

    // Mounting plate
    translate([0, -handle_len/2 - 10, 0])
        cube([platform_w/2, 5, 8], center=true);
}

module support_brackets() {
    // Side support brackets
    for (side = [-1, 1]) {
        color("Silver")
        translate([side * (platform_w/2 + 2), 0, platform_h/2 - 3])
            rotate([0, 90, 0])
                cylinder(h=8, r=2, center=true, $fn=16);

        color("Silver")
        translate([side * (platform_w/2 + 2), 0, platform_h/2 - 3])
            cube([4, 6, 8], center=true);
    }

    // Rear support
    color("Silver")
    translate([0, platform_l/2 + 2, platform_h/2 - 3])
        rotate([90, 0, 0])
            cylinder(h=6, r=2, center=true, $fn=16);

    color("Silver")
    translate([0, platform_l/2 + 2, platform_h/2 - 3])
        cube([platform_w - 4, 4, 8], center=true);
}

