handle_t = 3;

// Central handle
module handle_body() {
    color(color_handle)
    difference() {
        hull() {
            // Ergonomic oval handle
            translate([-3,0,0]) cylinder(h = 70, d = 18, center = true);
            translate([ 3,0,0]) cylinder(h = 70, d = 18, center = true);
        }
        // Grip cavity
        translate([0,0,0])
            cube([22, 22, 72], center = true);
    }
}

// Rubberised grip inserts
module grip_strips() {
    color(color_grip)
    for (z = [-25, -15, -5, 5, 15, 25]) {
        translate([0, 0, z])
            scale([1, 0.7, 1])
                cylinder(h = 6, d = 20, $fn = 60);
    }
}

// Blade profile helper
module half_blade(radius, width) {
    difference() {
        // Semi-cylindrical blade
        intersection() {
            cylinder(h = width, d = radius*2, center = true);
            translate([0, -radius, 0]) cube([radius*2, radius, width+0.1], center = true);
        }
        // Hollow underside to reduce material and avoid overhang
        translate([0, 0, -width/2-0.1])
            cube([radius*2-1, radius-0.8, width+0.2], center = true);
    }
}

// Blade with shaft
module blade(size, width) {
    color(color_steel)
    union() {
        // Shaft
        translate([0, 0, 0])
            cube([blade_t, 12, width], center = true);

        // Curved blade end
        translate([0, -12/2 - size/2, 0])
            rotate([90, 0, 0])
                half_blade(size, width);
    }
}

// Assembly
module scaffold_tool() {
    // Handle
    handle_body();
    grip_strips();

    // Left blade (small)
    translate([0, 9.5, -30])
        blade(6, 15);

    // Right blade (large)
    translate([0, 9.5, 30])
        blade(10, 15);
