color_wheel = [0.1, 0.1, 0.1];   // Black tire
color_axle = [0.5, 0.5, 0.5];    // Gray metal

// Dimensions (mm)
tray_length = 60;
tray_width = 40;
tray_height = 15;
handle_length = 55;
handle_diameter = 4;
wheel_radius = 12;
wheel_width = 6;
leg_height = 20;

module tray() {
    // Main tray body
    color(color_body)
    difference() {
        union() {
            // Base tray
            translate([-tray_length/2, -tray_width/2, 0])
                cube([tray_length, tray_width, tray_height]);

            // Front slope
            translate([-tray_length/2 - 8, -tray_width/2, tray_height - 3])
                rotate([90, 0, 0])
                    cylinder(h=10, r1=10, r2=8);
            translate([-tray_length/2 - 8, tray_width/2, tray_height - 3])
                rotate([90, 0, 0])
                    cylinder(h=10, r1=10, r2=8);
        }

        // Hollow out the tray
        translate([-tray_length/2 + 2, -tray_width/2 + 2, 2])
            cube([tray_length - 4, tray_width - 4, tray_height + 5]);

        // Drain hole
        translate([tray_length/2 - 8, 0, 1])
            cylinder(h=3, r=2);
    }

    // Reinforcement ribs
    for(i = [-1, 0, 1]) {
        translate([i * 15, -tray_width/2 - 1, 0])
            cube([2, tray_width + 2, tray_height]);
    }
}

module handles() {
    color(color_handle)
    union() {
        // Left handle
        translate([-tray_length/2 + 5, -tray_width/2 - 8, tray_height - 2])
            rotate([0, 70, 0])
                cylinder(h=handle_length, d=handle_diameter);

        // Right handle
        translate([-tray_length/2 + 5, tray_width/2 + 8, tray_height - 2])
            rotate([0, 70, 0])
                cylinder(h=handle_length, d=handle_diameter);

        // Cross brace
        translate([-tray_length/2 + 12, -tray_width/2 - 8, tray_height + 15])
            rotate([90, 0, 90])
                cylinder(h=tray_width + 16, d=handle_diameter * 0.8);

        // Grips
        translate([-tray_length/2 + 5, -tray_width/2 - 8 - handle_length/2, tray_height - 2 + handle_length/2 * sin(70)])
            rotate([0, 90, 0])
                cylinder(h=8, d=handle_diameter * 1.2);
        translate([-tray_length/2 + 5, tray_width/2 + 8 + handle_length/2, tray_height - 2 + handle_length/2 * sin(70)])
            rotate([0, 90, 0])
                cylinder(h=8, d=handle_diameter * 1.2);
    }
}

module wheel() {
    color(color_wheel)
    union() {
        // Tire
        difference() {
            cylinder(h=wheel_width, r=wheel_radius, center=true);
            cylinder(h=wheel_width + 1, r=wheel_radius - 3, center=true);
        }

        // Hub
        color(color_axle)
        cylinder(h=wheel_width + 2, r=3, center=true);

        // Spokes
        for(i = [0:6]) {
            rotate([0, 0, i * 60])
                translate([wheel_radius/2, 0, 0])
                    cube([wheel_radius - 2, 1, wheel_width], center=true);
        }
    }
}

module legs() {
    color(color_axle)
    union() {
        // Left leg
        translate([-tray_length/2 + 10, -tray_width/2 - 2, 0])
            cube([2, 4, leg_height]);

        // Right leg
        translate([-tray_length/2 + 10, tray_width/2 - 2, 0])
            cube([2, 4, leg_height]);

        // Support struts
        translate([-tray_length/2 + 5, -tray_width/4, 0])
            rotate([0, -30, 0])
                cube([2, 2, 15]);
        translate([-tray_length/2 + 5, tray_width/4, 0])
            rotate([0, 30, 0])
                cube([2, 2, 15]);
    }
}

module axle() {
    color(color_axle)
    cylinder(h=tray_width + 20, r=2, center=true);
}

// Assemble the barrow
union() {
    tray();
    handles();
    legs();

    // Wheel assembly
    translate([tray_length/2 - 5, 0, wheel_radius + 2])
        rotate([90, 0, 0])
            axle();

    translate([tray_length/2 - 5, 0, wheel_radius + 2])
        rotate([90, 0, 0])
            wheel();
}

