module wheel() {
    color("dimgray")
    difference() {
        cylinder(h = wheel_w, d = wheel_d, $fn = 30);
        // axle hole
        translate([0, 0, -1])
            cylinder(h = wheel_w + 2, d = axle_d, $fn = 20);
    }
}

module axle() {
    color("silver")
    rotate([90, 0, 0])
        cylinder(h = bed_w + 2 * wheel_w, d = axle_d, $fn = 12);
}

module wheels_assembly() {
    translate([-bed_l/2 - wheel_w/2, 0, wheel_d/2])
        wheel();
    translate([-bed_l/2 - wheel_w/2, 0, wheel_d/2])
        axle();
    translate([-bed_l/2 - wheel_w/2, 0, wheel_d/2])
        mirror([0, 1, 0])
            wheel();
}

module wagon_bed() {
    color("firebrick")
    difference() {
        // outer bed
        translate([0, 0, bed_h/2])
            cube([bed_l, bed_w, bed_h], center = true);
        // hollow inside
        translate([0, 0, bed_h/2 + wall_t/2])
            cube([bed_l - 2 * wall_t, bed_w - 2 * wall_t, bed_h], center = true);
        // front slope
        translate([bed_l/2 - wall_t, 0, bed_h/2 + wall_t/2])
            rotate([0, 45, 0])
                cube([bed_h * 1.5, bed_w, bed_h * 1.5], center = true);
    }
}

module side_rail() {
    color("firebrick")
    translate([0, bed_w/2 - wall_t/2, bed_h + wall_t/2])
        cube([bed_l, wall_t, wall_t * 2]);
