    rotate([90, 0, 0])
        cylinder(h = handle_len, d = handle_rod_d, $fn = 12);
}

module handle_grip() {
    color("black")
    rotate([90, 0, 0])
        cylinder(h = handle_grip_l, d = handle_grip_d, $fn = 20);
}

module handle_assembly() {
    translate([-bed_l/2 - handle_len, 0, bed_h - handle_rod_d/2]) {
        handle_rod();
        translate([0, 0, handle_len - handle_grip_l/2])
            handle_grip();
    }
}

module wagon_tool() {
    union() {
        wagon_bed();
        side_rail();
        mirror([0, 1, 0])
            side_rail();
        wheels_assembly();
        mirror([1, 0, 0])
            wheels_assembly();
        handle_assembly();
    }
}

