        cube([plank_length, plank_width, plank_thickness], center=true);

        // Rounded edges - remove corners
        translate([plank_length/2 - 2, 0, 0])
            rotate([0, 90, 0])
                cylinder(h=plank_width, r=2, center=true);
        translate([-plank_length/2 + 2, 0, 0])
            rotate([0, 90, 0])
                cylinder(h=plank_width, r=2, center=true);
    }
