    color(metal_color)
    for(i = [0:cutting_teeth_count-1]) {
        translate([
            -plank_length/2 + 5 + (i * (plank_length-10)/(cutting_teeth_count-1)),
            plank_width/2 - 1,
            0
        ]) {
            rotate([0, 0, 45])
                cube([3, 3, plank_thickness], center=true);
            rotate([0, 0, -45])
                cube([3, 3, plank_thickness], center=true);
