    difference() {
        // Main cylinder
        cylinder(h=handle_len, d=handle_d, center=true);

        // Flattened ends for finger comfort
        translate([0,0,handle_len/2])
            cube([handle_d*1.1, handle_d*1.1, 2], center=true);
        translate([0,0,-handle_len/2])
            cube([handle_d*1.1, handle_d*1.1, 2], center=true);
