        cube([fork_tine_thickness*2, fork_tine_thickness*2, 4], center=true);
    }
}

// Fork head module
module fork_head() {
    color(color_fork)
    union() {
        // Base plate
        translate([0, 0, -2])
        cube([fork_width, fork_tine_thickness*2, 4], center=true);

        // Tines
        spacing = fork_width / (fork_tine_count + 1);
        for(i = [1:fork_tine_count]) {
            translate([-fork_width/2 + i*spacing, 0, 0])
            tine();
        }

        // Reinforcement rib
        translate([0, 0, -5])
        cube([fork_width*0.8, 5, 6], center=true);
    }
}

// Handle shaft module
module handle_shaft() {
    color(color_handle)
    union() {
        // Main shaft
        cylinder(h=handle_length, d=handle_diameter, $fn=32);

        // End cap
        translate([0, 0, handle_length])
        cylinder(h=5, d1=handle_diameter, d2=handle_diameter*1.2, $fn=32);
    }
}

// Assembly
union() {
    // Handle assembly
    translate([0, 0, handle_length/2])
    rotate([90, 0, 0])
    handle_shaft();

    // Grip
    translate([0, 0, handle_length-15])
    rotate([90, 0, 0])
    grip();

    // Fork attached to handle end
    translate([0, 0, 0])
    rotate([90, 0, 0])
    translate([0, handle_length, 0])
    fork_head();
}

