}

// Peat Spade (Turf Cutter) – OpenSCAD
// Realistic, medium complexity
// Overall length ~180 mm, scaled for 3D printing

// Parameters
handle_len = 90;
handle_d = 12;
shaft_len = 40;
shaft_d = 16;
blade_h = 8;
blade_w = 40;
blade_l = 55;
grip_ring_d = 14;
grip_ring_h = 6;
rivet_r = 1.5;
rivet_h = 2;

// Colors
color_handle = "SaddleBrown";
color_shaft = "DarkOliveGreen";
color_blade = "Gray";

// Main assembly
difference() {
    union() {
        // Handle
        color(color_handle)
        translate([0, 0, 0])
        cylinder(h = handle_len, d = handle_d, $fn = 32);

        // Grip rings on handle
        for (i = [1:3]) {
            color(color_handle)
            translate([0, 0, handle_len * 0.15 * i])
            cylinder(h = grip_ring_h, d = grip_ring_d, $fn = 32);
        }

        // Shaft
        color(color_shaft)
        translate([0, 0, handle_len])
        cylinder(h = shaft_len, d = shaft_d, $fn = 32);

        // Blade body
        color(color_blade)
        translate([0, 0, handle_len + shaft_len])
        linear_extrude(height = blade_h)
        polygon(points = [
            [-blade_w/2, 0],
            [blade_w/2, 0],
            [blade_w/2*0.8, blade_l],
            [-blade_w/2*0.8, blade_l]
        ]);

        // Blade edge
        color("DarkGray")
        translate([0, 0, handle_len + shaft_len])
        linear_extrude(height = blade_h)
        polygon(points = [
            [-blade_w/2*0.8, blade_l],
            [blade_w/2*0.8, blade_l],
            [0, blade_l + 6]
        ]);

        // Rivets fixing shaft to blade
        for (x = [-shaft_d/3, shaft_d/3]) {
            color("DarkGray")
            translate([x, 0, handle_len + shaft_len - rivet_h/2])
            rotate([90, 0, 0])
            cylinder(h = rivet_h, r = rivet_r, $fn = 12);
        }
    }

    // Hollow shaft for weight reduction
    color(color_shaft)
    translate([0, 0, handle_len + 2])
    cylinder(h = shaft_len - 4, d = shaft_d - 4, $fn = 32);

    // Handle cavity
    translate([0, 0, 2])
    cylinder(h = handle_len - 4, d = handle_d - 4, $fn = 32);
}

// Coir Tool (Coconut Fiber Extractor) - Realistic Style, Medium Complexity
// This is a traditional South-Indian coir tool with handle, blade, and collection mechanism

// Tool dimensions (in mm)
handle_length = 80;
handle_diameter = 12;
blade_width = 25;
blade_height = 40;
blade_thickness = 3;
collection_box_length = 35;
collection_box_width = 20;
collection_box_height = 15;

// Colors
wood_color = [139/255, 69/255, 19/255];  // Dark wood
metal_color = [192/255, 192/255, 192/255];  // Steel
leather_color = [101/255, 67/255, 33/255];  // Dark leather

module handle() {
    // Main handle with ergonomic grip
    color(wood_color) {
        // Handle shaft
        cylinder(h=handle_length, d=handle_diameter, $fn=20);

        // Grip rings for better hold
        for(i = [0:8]) {
            translate([0, 0, i * 8 + 5]) {
                cylinder(h=3, d=handle_diameter + 2, $fn=16);
            }
        }

        // End cap
        translate([0, 0, handle_length]) {
            sphere(d=handle_diameter + 4, $fn=20);
        }
    }
}

module blade() {
    color(metal_color) {
        // Main blade body
        translate([0, 0, -blade_height/2]) {
            cube([blade_thickness, blade_width, blade_height], center=true);
        }

        // Curved cutting edge
        translate([blade_thickness/2 + 0.5, 0, -blade_height/2 - 5]) {
            rotate([0, 90, 0]) {
                cylinder(h=blade_width + 5, d=blade_height + 10, center=true, $fn=100);
            }
        }

        // Blade reinforcement ribs
        for(i = [-1, 1]) {
            translate([0, i * blade_width/4, 0]) {
                cube([blade_thickness + 2, 2, blade_height - 5], center=true);
            }
        }
    }
}

module collection_box() {
    color(leather_color) {
        // Main box
        translate([0, -blade_width/2 - collection_box_width/2 - 5, -collection_box_height/2]) {
            difference() {
                cube([collection_box_length, collection_box_width, collection_box_height], center=false);

                // Hollow interior
                translate([2, 2, 2]) {
                    cube([collection_box_length - 4, collection_box_width - 4, collection_box_height - 2], center=false);
                }
            }

            // Drainage holes
            for(i = [1:3]) {
                for(j = [1:2]) {
                    translate([i * 10, j * 7, -1]) {
                        cylinder(h=3, d=2, $fn=10);
                    }
                }
            }
        }
    }
}

module mounting_bracket() {
    color(metal_color) {
        // Bracket to attach blade to handle
        translate([handle_diameter/2, 0, 0]) {
            rotate([90, 0, 0]) {
                cube([handle_diameter, blade_thickness + 4, 10], center=true);
            }

            // Mounting screws
            for(i = [-1, 1]) {
                translate([handle_diameter/2 + 2, i * 3, 0]) {
                    rotate([90, 0, 0]) {
                        cylinder(h=8, d=2, $fn=8);
                    }
                }
            }
        }
    }
}

// Assembly
union() {
    // Handle
    handle();

    // Mounting bracket
    translate([0, 0, handle_length/2 + 10]) {
        mounting_bracket();
    }

    // Blade
    translate([handle_diameter/2 + blade_thickness/2 + 2, 0, handle_length/2 + 10]) {
        blade();
    }

    // Collection box
    translate([handle_diameter/2 + blade_thickness/2 + 2, 0, handle_length/2 + 10 - blade_height/2 + collection_box_height/2]) {
        collection_box();
    }
}

// Tool identification marking
