use <colors.scad>   // Standard color names

$fn = 36;

// Main assembly
union() {
    // Handle
    color("black")
    translate([0,0,0])
        handle_assembly();

    // Shaft
    color("silver")
    translate([0,0,35])
        shaft_assembly();

    // Paddle head
    color("silver")
    translate([0,0,75])
        paddle_assembly();
}

// ---------- Modules ----------

module handle_assembly() {
    // Main grip
    difference() {
        cylinder(h = 35, d = 20, center = false);
        // Light knurling grooves
        for (i = [0:8]) {
            rotate([0,0,i*22.5])
            translate([9.5,0,2])
                cube([1, 0.5, 31]);
        }
    }
    // End cap with slight fillet
    translate([0,0,35])
        cylinder(h = 3, d1 = 20, d2 = 18, center = false);
}

module shaft_assembly() {
    // Straight rod
    cylinder(h = 40, d = 8, center = false);

    // Reinforcement collar at bottom
    translate([0,0,0])
        cylinder(h = 4, d = 12, center = false);

    // Reinforcement collar at top
    translate([0,0,36])
        cylinder(h = 4, d = 12, center = false);
}

module paddle_assembly() {
    // Central hub
    cylinder(h = 6, d = 10, center = false);

    // Four curved mixing blades
    for (a = [0:90:270]) {
        rotate([0,0,a])
        translate([0,0,3])
            single_blade();
    }

    // Drip ring under blades
    translate([0,0,-2])
        cylinder(h = 2, d = 50, center = false);
}

module single_blade() {
    // Blade profile: curved and twisted
    difference() {
        union() {
            // Main blade body
            translate([0,12,0])
                rotate([0,0,-15])
                scale([1,1.2,0.8])
                cylinder(h = 15, d = 6, center = true);

            // Outer tip flare
            translate([0,24,0])
                rotate([0,0,-25])
                scale([1.2,1,1])
                sphere(d = 7);
        }

        // Hollow underside to reduce weight
        translate([0,12,-1])
            rotate([0,0,-15])
            scale([0.6,1,1.2])
            cylinder(h = 18, d = 4, center = true);
    }
}

// Realistic Blend Tool (Blender) - Medium Complexity
// Overall dimensions: ~85mm height, ~70mm width

// Color constants
color_body = [0.2, 0.2, 0.2];  // Dark grey
color_jar = [0.8, 0.8, 0.9];  // Light frosted glass
color_blades = [0.6, 0.6, 0.6];  // Metallic
color_controls = [0.1, 0.1, 0.1];  // Black
color_accents = [0.9, 0.1, 0.1];  // Red accents

// Base module with motor housing
module base() {
    color(color_body)
    union() {
        // Main base cylinder
        cylinder(h=35, d=65, $fn=64);

        // Control panel area
        translate([0, -25, 35])
            cube([40, 12, 15], center=true);

        // Control buttons
        translate([-15, -25, 43]) {
            color(color_controls)
            cylinder(h=3, d=8, $fn=32);
            translate([10, 0, 0])
                cylinder(h=3, d=8, $fn=32);
            translate([20, 0, 0])
                cylinder(h=3, d=8, $fn=32);
        }

        // Power indicator light
        translate([0, -30, 43]) {
            color(color_accents)
            sphere(d=4);
        }
    }
}

// Jar with measurement markings
module jar() {
    color(color_jar)
    difference() {
        // Main jar body
        cylinder(h=50, d1=55, d2=45, $fn=64);

        // Hollow interior
        translate([0, 0, -1])
            cylinder(h=52, d1=50, d2=40, $fn=64);

        // Measurement markings
        for(i = [1:4]) {
            translate([23, 0, i*10])
                rotate([90, 0, 0])
                    cube([0.5, 5, 1], center=true);
        }
    }
}

// Blade assembly
module blades() {
    color(color_blades)
    union() {
        // Central hub
        cylinder(h=15, d=15, $fn=32);

        // Four blade arms
        for(i = [0:3]) {
            rotate([0, 0, i*90]) {
                // Blade base
                translate([7.5, 0, 7.5])
                    rotate([90, 0, 0])
                        cube([20, 3, 1.5], center=true);

                // Blade tip
                translate([17.5, 0, 7.5])
                    rotate([90, 0, 0])
                        cube([5, 2, 1], center=true);
            }
        }
    }
}

// Lid with handle
module lid() {
    color(color_body)
    union() {
        // Main lid disk
        cylinder(h=3, d=50, $fn=64);

        // Handle grip
        translate([0, 0, 10])
            cylinder(h=15, d=20, $fn=32);

        // Grip ridges
        for(i = [0:7]) {
            rotate([0, 0, i*45])
                translate([10, 0, 17.5])
                    cube([0.5, 4, 15], center=true);
        }
    }
}

// Assembly
translate([0, 0, 0]) base();
translate([0, 0, 35]) jar();
translate([0, 0, 45]) blades();
translate([0, 0, 90]) lid();

// Seed Planting Tool (Realistic Style)
// Multi-function dibber with depth markings and ergonomic handle

// Global parameters
handle_length = 85;
handle_diameter = 25;
shaft_length = 60;
shaft_diameter = 15;
tip_length = 25;
wall_thickness = 3;
grip_depth = 1.5;

// Handle module with ergonomic grip
module handle() {
    color("BurlyWood")
    union() {
        // Main handle body
        cylinder(h=handle_length, d=handle_diameter, $fn=32);

        // Finger grooves
        for(i = [0:3]) {
            rotate([0, 0, i * 90])
            translate([0, -handle_diameter/2 + grip_depth, handle_length/2])
            difference() {
                cylinder(h=handle_length * 0.8, d=handle_diameter + 2, $fn=32);
                translate([0, 0, -1])
                cylinder(h=handle_length * 0.8 + 2, d=handle_diameter - grip_depth * 2, $fn=32);
            }
        }

        // End cap
        translate([0, 0, handle_length])
        cylinder(h=5, d=handle_diameter + 4, $fn=32);

        // Hanging hole
        translate([0, 0, handle_length - 10])
        rotate([90, 0, 0])
        cylinder(h=handle_diameter, d=6, $fn=16);
    }
}

// Shaft with depth markings
module shaft() {
    color("Silver")
    union() {
        // Main shaft
        cylinder(h=shaft_length + tip_length, d=shaft_diameter, $fn=24);

        // Depth markings (every 1cm)
        for(depth = [10:10:shaft_length]) {
            translate([0, 0, depth])
            difference() {
                cylinder(h=2, d=shaft_diameter + 4, $fn=24);
                cylinder(h=3, d=shaft_diameter - 1, $fn=24);
            }
        }

        // Measurement numbers (raised bumps)
        for(depth = [20:20:shaft_length]) {
            translate([shaft_diameter/2 + 2, -2, depth - 1])
            cube([4, 4, 2]);
            translate([shaft_diameter/2 + 2, -2, depth - 1])
            cube([2, 2, 4]);
        }
    }
}

// Pointed tip for soil penetration
module tip() {
    color("DarkGray")
    translate([0, 0, shaft_length])
    cylinder(h=tip_length, d1=shaft_diameter, d2=1, $fn=16);
}

// Seed dispenser mechanism
module seed_dispenser() {
    color("Gold")
    translate([0, 0, shaft_length - 15])
    difference() {
        // Main chamber
        sphere(d=shaft_diameter + 8);

        // Seed storage cavity
        sphere(d=shaft_diameter + 4);

        // Dispensing holes
        rotate([0, 90, 0])
        for(angle = [45:90:315]) {
            rotate([0, 0, angle])
            translate([0, 0, -shaft_diameter/2])
            cylinder(h=shaft_diameter, d=3, $fn=8);
        }
    }
}

// Assembly
union() {
    // Handle
    handle();

    // Transition from handle to shaft
    translate([0, 0, handle_length - 5])
    color("Brown")
    cylinder(h=10, d1=handle_diameter, d2=shaft_diameter + 4, $fn=32);

    // Shaft and tip
    translate([0, 0, handle_length + 5])
    union() {
        shaft();
        tip();
        seed_dispenser();
    }
}

// Grip enhancement rings
color("Black")
for(pos = [15:20:handle_length - 20]) {
    translate([0, 0, pos])
    difference() {
        cylinder(h=3, d=handle_diameter + 2, $fn=32);
        cylinder(h=4, d=handle_diameter - 2, $fn=32);
    }
}

// OpenSCAD Bulb Tool - Realistic Style, Medium Complexity
// Designed for 3D printing - approximately 80mm total length

// Bulb parameters
bulb_radius = 18;
neck_length = 12;
neck_radius = 8;
base_height = 15;
base_radius = 12;
thread_height = 2;
thread_depth = 1;
handle_length = 45;
handle_diameter = 15;
grip_length = 30;

// Main module
module bulb_tool() {
    color("ivory") {
        // Glass bulb with slight taper
        translate([0, 0, base_height + neck_length + bulb_radius/2]) {
            sphere(r = bulb_radius, $fn = 32);
        }

        // Neck connecting bulb to base
        translate([0, 0, base_height + neck_length/2]) {
            cylinder(h = neck_length, r1 = neck_radius, r2 = neck_radius * 0.9, $fn = 32);
        }
    }

    // Metal base with threads
    color("silver") {
        difference() {
            // Main base cylinder
            cylinder(h = base_height, r = base_radius, $fn = 32);

            // Thread grooves
            for(i = [0:3]) {
                translate([0, 0, i * thread_height + 1]) {
                    rotate([0, 0, i * 30]) {
                        cylinder(h = thread_height + 0.1, r = base_radius - thread_depth, $fn = 32);
                    }
                }
            }
        }
    }

    // Handle with grip texture
    color("black") {
        // Main handle
        translate([0, 0, -handle_length]) {
            cylinder(h = handle_length - 5, r = handle_diameter/2, $fn = 32);
        }

        // Grip texture rings
        for(i = [0:5]) {
            translate([0, 0, -handle_length + 5 + i * 5]) {
                difference() {
                    cylinder(h = 3, r = handle_diameter/2 + 1, $fn = 32);
                    cylinder(h = 3.1, r = handle_diameter/2 - 1, $fn = 32);
                }
            }
        }
    }

    // End cap
    color("silver") {
        translate([0, 0, -handle_length - 5]) {
            cylinder(h = 5, r = handle_diameter/2 + 2, $fn = 32);
        }
    }

    // Filament support wires inside bulb
    color("gray") {
        translate([0, 0, base_height + neck_length + bulb_radius/2]) {
            for(i = [0:2]) {
                rotate([0, 0, i * 120]) {
                    translate([bulb_radius * 0.3, 0, 0]) {
                        cylinder(h = bulb_radius * 0.8, r = 0.5, $fn = 8);
                    }
                }
            }
        }
    }
}

// Generate the complete tool
bulb_tool();

// Tuber Tool - Potato Eye Remover
// Realistic style, medium complexity

$fn = 30;

// Main dimensions
handle_length = 80;
handle_diameter = 20;
head_diameter = 15;
blade_depth = 8;
blade_diameter = 12;

// Colors
handle_color = "SaddleBrown";
metal_color = "Silver";
blade_color = "LightGray";

// Handle grip texture
module grip_texture() {
    for (i = [0:9]) {
        rotate([0, 0, i * 36]) 
            translate([0, handle_diameter/2 - 1, 0])
                color(handle_color)
                    cube([handle_diameter * 0.8, 2, handle_length/10], center=true);
    }
}

// Handle with ergonomic shape
module handle() {
    color(handle_color) {
        // Main handle body
        cylinder(h=handle_length, d1=handle_diameter, d2=handle_diameter * 0.9);

        // Grip rings
        for (i = [1:3:handle_length-10]) {
            translate([0, 0, i])
                cylinder(h=3, d=handle_diameter + 2, center=true);
        }

        // End cap
        translate([0, 0, handle_length])
            sphere(d=handle_diameter);
    }

    // Grip texture
    translate([0, 0, handle_length/2])
        grip_texture();
}

// Tool head with cutter mechanism
module tool_head() {
    // Head base
    color(metal_color) {
        translate([0, 0, -5])
            cylinder(h=10, d=head_diameter);
    }

    // Blade assembly
    color(blade_color) {
        // Cylindrical cutter
        translate([0, 0, -blade_depth - 2])
            cylinder(h=blade_depth, d=blade_diameter);

        // Inner cutting edge
        translate([0, 0, -blade_depth - 2.5])
            cylinder(h=blade_depth + 1, d=blade_diameter - 2);

        // Pointed tip
        translate([0, 0, -blade_depth - 2])
            cylinder(h=5, d1=blade_diameter, d2=0);
    }

    // Reinforcement ring
    color(metal_color) {
        translate([0, 0, -2])
            cylinder(h=3, d=head_diameter + 4);
    }
}

// Assembly
union() {
    // Handle
    translate([0, 0, 0])
        handle();

    // Tool head
    translate([0, 0, 0])
        tool_head();
}

// Hanging loop
color(metal_color) {
    translate([handle_diameter/2 + 3, 0, handle_length - 15])
        rotate([90, 0, 0])
            difference() {
                cylinder(h=5, d=10);
                translate([0, 0, -1])
                    cylinder(h=7, d=6);
            }
}

// Corm tool - medium complexity realistic style

module handle() {
    // Main grip with ergonomic curves
    color([0.15, 0.15, 0.15]) {
        // Handle grip
        difference() {
            cylinder(h=60, d=25, $fn=32);
            // Finger grooves
            for(i=[-20:10:20]) {
                translate([0, i, 30]) rotate([0,90,0]) 
                    cylinder(h=15, d1=20, d2=25, $fn=16);
            }
        }

        // Handle cap
        translate([0,0,60]) cylinder(h=5, d=28, $fn=32);
    }

    // Rubber grip insert
    color([0.1, 0.1, 0.1]) {
        translate([0,0,25]) cylinder(h=30, d=22, $fn=32);
    }
}

module shaft() {
    // Metal shaft
    color([0.7, 0.7, 0.7]) {
        cylinder(h=80, d=8, $fn=16);

        // Reinforcement ring
        translate([0,0,75]) cylinder(h=5, d=12, $fn=16);
    }
}

module cutter_assembly() {
    // Main cutter body
    color([0.6, 0.6, 0.6]) {
        translate([0,0,80]) cylinder(h=15, d=20, $fn=32);

        // Cutter blades (4 curved blades)
        for(i=[0:90:359]) {
            rotate([0,0,i]) translate([8,0,87]) 
                rotate([0,90,0]) 
                    cylinder(h=12, d1=4, d2=2, $fn=8);
        }

        // Center spike
        translate([0,0,85]) cylinder(h=10, d=3, $fn=16);
    }

    // Blade edges
    color([0.8, 0.8, 0.8]) {
        for(i=[0:90:359]) {
            rotate([0,0,i]) translate([8,0,87]) 
                rotate([0,90,0]) 
                    cylinder(h=11, d1=3.5, d2=1.5, $fn=8);
        }
    }
}

module safety_cover() {
    // Protective cover (removable)
    color([0.2, 0.2, 0.3, 0.6]) {
        difference() {
            // Main cover shape
            cylinder(h=25, d=24, $fn=32);

            // Hollow center
            cylinder(h=27, d=18, $fn=32);

            // Grip slots
            for(i=[0:120:359]) {
                rotate([0,0,i]) translate([11,0,12]) 
                    cube([2, 8, 25], center=true);
            }
        }
    }
}

// Assembly
handle();
translate([0,0,60]) shaft();
translate([0,0,140]) cutter_assembly();

