// Rhizome Tool - Realistic Style
// A specialized gardening tool for harvesting and dividing rhizomes

// Tool dimensions (mm)
handle_length = 120;
handle_diameter = 25;
blade_length = 80;
blade_width = 35;
blade_thickness = 3;
tang_length = 40;
ferrule_length = 20;
ferrule_diameter = 28;

// Colors
wood_color = "#8B4513";
metal_color = "#C0C0C0";
grip_color = "#2F4F4F";

// Handle with ergonomic grip
module handle() {
    color(wood_color) {
        // Main handle body
        cylinder(h=handle_length, d=handle_diameter, $fn=32);

        // Ergonomic grip pattern
        for(i = [10:20:handle_length-30]) {
            translate([0, 0, i])
            difference() {
                cylinder(h=5, d=handle_diameter+2, $fn=32);
                cylinder(h=5.1, d=handle_diameter-2, $fn=32);
            }
        }
    }

    // Rubber grip overlay
    color(grip_color) {
        translate([0, 0, handle_length/2])
        cylinder(h=handle_length/2+10, d=handle_diameter-2, $fn=32);
    }
}

// Metal ferrule connecting handle to blade
module ferrule() {
    color(metal_color) {
        cylinder(h=ferrule_length, d=ferrule_diameter, $fn=32);

        // Decorative ring
        translate([0, 0, ferrule_length-5])
        cylinder(h=2, d=ferrule_diameter+2, $fn=32);

        // Rivet details
        for(angle = [0:90:270]) {
            rotate([0, 0, angle])
            translate([ferrule_diameter/2-2, 0, ferrule_length/2])
            cylinder(h=ferrule_diameter, d=2, $fn=8);
        }
    }
}

// Curved blade for rhizome cutting
module blade() {
    color(metal_color) {
        // Blade tang
        translate([0, 0, -tang_length/2])
        cube([blade_thickness, blade_width/3, tang_length], center=true);

        // Main blade body
        translate([0, 0, blade_length/2])
        linear_extrude(height=blade_thickness, center=true, convexity=10)
        polygon(points=[
            [-blade_thickness/2, 0],
            [-blade_width/2, blade_length/4],
            [-blade_width/2+5, blade_length],
            [blade_width/2-5, blade_length],
            [blade_width/2, blade_length/4],
            [blade_thickness/2, 0]
        ]);

        // Cutting edge bevel
        translate([0, 0, blade_length-5])
        rotate([90, 0, 0])
        cylinder(h=blade_width-10, r1=blade_thickness, r2=0, center=true);

        // Blade serrations for root cutting
        for(i = [10:5:blade_length-10]) {
            translate([0, 0, i])
            difference() {
                cube([blade_thickness+1, blade_width-10, 2], center=true);
                cube([blade_thickness-1, blade_width-14, 2.1], center=true);
            }
        }
    }
}

// Assemble the tool
translate([0, 0, handle_length/2])
handle();

translate([0, 0, -ferrule_length/2])
ferrule();

translate([0, 0, -ferrule_length-tang_length/2])
rotate([0, 90, 0])
blade();

// Realistic medium-complexity 3D-printable box-cutter (utility knife)
// Overall length ~95 mm, blade retracts, grip textures, pocket clip

// Colors
blade_color   = [0.7,0.7,0.7];   // steel
body_color    = [0.1,0.1,0.1];   // black ABS
grip_color    = [0.15,0.15,0.15];
clip_color    = [0.8,0.8,0.8];

// Main body shell
module body_shell(){
    difference(){
        // Outer shell
        color(body_color)
        hull(){
            translate([-4,0,0]) cylinder(h=90,r=8,$fn=32);
            translate([ 4,0,0]) cylinder(h=90,r=8,$fn=32);
        }
        // Blade channel
        translate([0,0,-1]) cube([2.6,20,92],center=true);
        // Front slot for blade exit
        translate([0,0,44.5]) cube([3,12,5],center=true);
    }
}

// Rubber grip inserts
module grip_inserts(){
    color(grip_color)
    for(i=[-30:5:25])
        translate([0,9.5,i])
            rotate([0,90,0])
                cylinder(h=2,r=1,$fn=16);
}

// Blade with trapezoid tip
module blade(length=30){
    color(blade_color){
        linear_extrude(height=0.6){
            polygon(points=[
                [-15, 0],
                [length-5, 0],
                [length, 2],
                [length, 4],
                [length-5, 6],
                [-15, 6]
            ]);
        }
    }
}

// Thumb slider mechanism
module slider(){
    color(clip_color)
    union(){
        // Slider block
        translate([0,-7,0])
            cube([8,4,12],center=true);
        // Grip ridges
        for(i=[-4:2:4])
            translate([i,-5,0])
                cube([0.8,2,12],center=true);
    }
}

// Pocket clip
module pocket_clip(){
    color(clip_color)
    difference(){
        union(){
            // Clip arc
            translate([0,9,-35])
                rotate([90,0,0])
                    cylinder(h=40,r=2,$fn=32);
            // Mount base
            translate([0,9,-35])
                cube([3,4,10],center=true);
        }
        // Remove inner volume
        translate([0,9,-35])
            rotate([90,0,0])
                cylinder(h=45,r=1.2,$fn=32);
    }
}

// Assembly
body_shell();
grip_inserts();

// Blade in extended position
translate([0,0,30])
    rotate([0,90,0])
        blade(25);

// Slider attached to blade
translate([0,-7,30])
    slider();

pocket_clip();

// Slip Tool - Realistic Style with Medium Complexity
// Based on masonry jointing tool for finishing mortar joints

$fn = 48;  // Smooth curves for 3D printing

// Main colors
color_tool = "silver";
color_grip = "darkslategray";

// Overall dimensions (mm)
overall_length = 90;
handle_length = 60;
head_width = 25;
head_height = 15;

// Handle diameter for comfortable grip
handle_diameter = 12;

// Grip texture pattern
module grip_texture() {
    for (i = [0:5:handle_length-10]) {
        translate([0, 0, i]) {
            difference() {
                cylinder(h=2, d=handle_diameter + 2, center=true);
                cylinder(h=3, d=handle_diameter, center=true);
            }
        }
    }
}

// Handle with ergonomic grip
module handle() {
    color(color_grip) {
        // Main handle body
        difference() {
            cylinder(h=handle_length, d=handle_diameter, center=true);
            // Hollow core for weight reduction
            cylinder(h=handle_length + 2, d=6, center=true);
        }

        // Grip rings
        grip_texture();

        // End cap
        translate([0, 0, handle_length/2 + 1]) {
            cylinder(h=3, d=handle_diameter + 2);
        }
    }
}

// Curved slip head for concave joints
module curved_slip() {
    color(color_tool) {
        // Main curved head
        difference() {
            // Outer shape
            union() {
                // Curved blade
                translate([-head_width/2, 0, 0]) {
                    rotate([90, 0, 0]) {
                        cylinder(h=head_width, d=head_height);
                    }
                }

                // Transition to handle
                translate([0, 0, -head_height/2]) {
                    cylinder(h=head_height, d1=head_width, d2=handle_diameter);
                }
            }

            // Inner curve for joint profile
            translate([0, head_height/2 - 2, 0]) {
                rotate([90, 0, 0]) {
                    cylinder(h=head_width + 2, d=head_height - 4);
                }
            }

            // Remove back half for tool profile
