                rotate([0, 0, -45])
                    color("DarkGreen") 
                    cube([5, 3, blade_thickness+0.5], center=true);
            }
            mirror([0, 1, 0]) {
                translate([i-leaf_length/2, leaf_width/2, 0]) {
                    rotate([0, 0, -45])
                        color("DarkGreen") 
                        cube([5, 3, blade_thickness+0.5], center=true);
