        for(i = [-3:1:3]) {
            translate([0, 0, i*10 - 5])
            rotate([90, 0, 0])
            cylinder(h=handle_length + 5, d=handle_diameter - 2, center=true);
        }

        // Finger indentations
        for(angle = [0:60:300]) {
            rotate([0, 0, angle])
            translate([0, handle_diameter/2 - 2, 0])
            sphere(d=8);
        }
