// Tool head with picking prongs
module tool_head() {
    difference() {
        // Main tool body
        union() {
            // Base plate
            cube([tool_length, tool_width, 5], center=true);

            // Reinforcement ribs
            for(i = [-1:1:1]) {
                translate([i * 25, 0, -2.5])
                cube([5, tool_width - 10, 10], center=true);
            }

            // Mounting collar for handle
            translate([0, 0, -7.5])
            cylinder(h=10, d=handle_diameter + 10, center=true);
        }

        // Handle mounting hole
        translate([0, 0, -12.5])
        cylinder(h=20, d=handle_diameter, center=true);

        // Weight reduction holes
        for(x = [-30:15:30]) {
            for(y = [-15:15:15]) {
                translate([x, y, -2.5])
                cylinder(h=10, d=8);
            }
        }
    }

    // Picking prongs
    translate([tool_length/2 + 5, 0, 0])
    picking_prongs();
