// Comb-like picking prongs
module picking_prongs() {
    prong_length = 30;
    prong_width = 3;
    prong_thickness = 2;
    prong_count = 7;
    spacing = tool_width / (prong_count - 1);

    for(i = [0:1:prong_count-1]) {
        y_pos = -tool_width/2 + i * spacing;

        // Main prong
        translate([0, y_pos, 0])
        rotate([0, -30, 0])
        cube([prong_length, prong_width, prong_thickness], center=true);

        // Rounded prong tip
        translate([prong_length/2 + 1, y_pos, 0])
        rotate([0, -30, 0])
        sphere(d=prong_width);

        // Reinforcement web between prongs
        if(i < prong_count - 1) {
            translate([prong_length/4, y_pos + spacing/2, 0])
            rotate([0, -30, 0])
            cube([prong_length/2, spacing - prong_width, prong_thickness/2], center=true);
        }
    }
}

