        cube([basket_width - 2*wall_thickness, 
              basket_depth - 2*wall_thickness, 
              basket_height - wall_thickness], center=true);

        // Drainage holes
        for(x = [-10:10:10]) {
            for(z = [-10:10:10]) {
                translate([x, -basket_depth/2 - 1, z])
                cylinder(h=wall_thickness + 2, d=3);
