        // Main scoop body
        difference() {
            // Outer shape
            translate([0, scoop_width/2, 0])
            cube([scoop_length, scoop_width, scoop_height], center=false);

            // Taper the bottom for pouring
            translate([-5, 0, -5])
            rotate([0, spout_angle, 0])
            cube([scoop_length * 2, scoop_width * 2, scoop_height * 2], center=false);

