hole_spacing = 8;

// Main module
module herb_stripper() {
    color("ForestGreen")
    union() {
        // Handle
        handle();

        // Stripping head
        translate([handle_length - tool_thickness/2, 0, 0])
        rotate([0, 90, 0])
        stripping_head();
    }
