// Handle with grip rings
module handle() {
    color("DarkOliveGreen")
    union() {
        // Main handle body
        cylinder(h=handle_length, d=handle_diameter, $fn=32);

        // Grip rings
        for(i = [1:grip_ring_count]) {
            translate([0, 0, i * (handle_length/(grip_ring_count+1))])
            cylinder(h=2, d=handle_diameter + 2, $fn=32);
        }

        // End cap
        translate([0, 0, handle_length])
        cylinder(h=3, d=handle_diameter + 4, $fn=32);
