// Stripping head with holes
module stripping_head() {
    color("ForestGreen")
    difference() {
        // Main head body
        cube([tool_width, tool_thickness, tool_width], center=true);

        // Rounded edges for comfort
        translate([0, tool_thickness/2 + 0.1, 0])
        rotate([90, 0, 0])
        cylinder(h=tool_width, r=tool_thickness/2, center=true, $fn=32);

        // Stripping holes
        for(i = [0:len(hole_sizes)-1]) {
            translate([(i - (len(hole_sizes)-1)/2) * hole_spacing, 0, 0])
            rotate([0, 90, 0])
            cylinder(h=tool_thickness + 2, d=hole_sizes[i], center=true, $fn=32);
