            cylinder(h=5, d1=hole_sizes[i] + 3, d2=hole_sizes[i], $fn=32);
        }

        // Hanging hole
        translate([0, 0, -tool_width/2 - 1])
        rotate([90, 0, 0])
        cylinder(h=tool_thickness + 2, d=3, center=true, $fn=32);
    }

