color_chain = "Goldenrod";

// Handle with grip pattern
module handle() {
    color(color_handle) {
        // Main handle cylinder
        cylinder(h=handle_length, d=handle_diameter, center=false);

        // Grip rings
        for(i = [10:15:handle_length-20]) {
            translate([0, 0, i]) {
                difference() {
                    cylinder(h=3, d=handle_diameter + 2, center=false);
                    cylinder(h=3.5, d=handle_diameter - 1, center=false);
                }
            }
        }

        // Top cap
        translate([0, 0, handle_length]) {
            sphere(d=handle_diameter + 2);
        }
    }
}

// Perforated tea basket
module tea_basket() {
    color(color_basket) {
        difference() {
            // Main basket
            cylinder(h=basket_height, d=basket_diameter, center=true);

            // Hollow interior
            cylinder(h=basket_height - 2*wall_thickness, d=basket_diameter - 2*wall_thickness, center=true);

            // Perforations - small holes for water flow
            for(angle = [0:30:359]) {
                rotate([0, 0, angle]) {
                    for(h = [-15:10:15]) {
                        translate([basket_diameter/2 - wall_thickness, 0, h]) {
                            rotate([0, 90, 0]) {
                                cylinder(h=wall_thickness + 1, d=2, center=true);
                            }
                        }
                    }
                }
            }

            // Top opening
            translate([0, 0, basket_height/2]) {
                cylinder(h=wall_thickness + 1, d=basket_diameter - 2*wall_thickness, center=true);
            }
        }

        // Hinge for basket lid
        translate([0, -basket_diameter/2, basket_height/2 - 3]) {
            cube([2, 5, 2], center=true);
        }

        // Latch on opposite side
        translate([0, basket_diameter/2 - wall_thickness, basket_height/2 - 3]) {
            cube([2, wall_thickness, 2], center=true);
        }
    }
}

// Basket lid
module basket_lid() {
    color(color_basket) {
        difference() {
            // Main lid
            cylinder(h=wall_thickness, d=basket_diameter - 0.5, center=true);

            // Handle on lid
            translate([0, 0, wall_thickness/2]) {
                cylinder(h=5, d=5, center=false);
            }
        }
    }
}

// Individual chain link
