    color(color_chain) {
        difference() {
            // Main ring
            rotate([90, 0, 0]) {
                torus(r1=chain_link_size/2, r2=chain_link_size/6);
            }

            // Gap for assembly
            translate([-chain_link_size/2 - 1, 0, 0]) {
                cube([chain_link_size + 2, chain_link_size/3, chain_link_size/3], center=true);
            }
        }
    }
}

// Complete chain
module chain() {
    for(i = [0:chain_link_size*1.5:chain_length-chain_link_size*1.5]) {
        translate([0, 0, i]) {
            chain_link();
