// Main soap module
module soap_bar() {
    color(soap_color) {
        difference() {
            // Main soap body with rounded edges
            union() {
                // Core shape
                translate([0, 0, soap_height/2])
                    rounded_cube([soap_length, soap_width, soap_height], 5);

                // Grip ridges on sides
                for (i = [-1, 1]) {
                    translate([0, i*(soap_width/2 - wall_thickness/2), soap_height/2])
                        rounded_cube([soap_length*0.8, wall_thickness, soap_height - 4], 2);
                }
            }

            // Embossed logo (simple wave pattern)
            translate([0, 0, soap_height - logo_height - 0.1])
                linear_extrude(height=logo_height + 0.2) {
                    translate([0, 0, 0])
                        text("SOAP", size=12, halign="center", valign="center");
                }

            // Drainage grooves on bottom
            for (i = [-20, 0, 20]) {
                translate([i, 0, -0.1])
                    cube([3, soap_width - 10, 2]);
            }
        }
    }
