// Drainage tray module
module soap_tray() {
    color(tray_color) {
        difference() {
            // Main tray base
            union() {
                // Base plate
                translate([0, 0, tray_height/2])
                    rounded_cube([tray_length, tray_width, tray_height], 3);

                // Raised edges
                translate([0, 0, tray_height + wall_thickness/2])
                    rounded_cube([tray_length, tray_width, wall_thickness], 3);
            }

            // Central cavity for soap
            translate([0, 0, tray_height - 0.1])
                rounded_cube([soap_length + 4, soap_width + 4, tray_height + 0.2], 4);

            // Drainage holes
            for (x = [-30, -10, 10, 30]) {
                for (y = [-15, 0, 15]) {
                    translate([x, y, 0])
                        cylinder(h=tray_height + 1, r=2, $fn=16);
                }
            }
        }
    }
}

// Helper module for rounded cubes
module rounded_cube(size, radius) {
    hull() {
        for (x = [-1, 1]) {
            for (y = [-1, 1]) {
                for (z = [-1, 1]) {
                    translate([x*(size[0]/2 - radius), y*(size[1]/2 - radius), z*(size[2]/2 - radius)])
                        sphere(r=radius, $fn=16);
                }
            }
        }
    }
}

