    translate([0, 0, 0])
        case_bottom();

    // Case top with mirror
    translate([0, case_diameter * 0.8, case_height])
        rotate([0, 180, 0])
        case_top();

    // Puff inside
    translate([0, 0, wall_thickness + 2])
        puff();

    // Hinge mechanism
    translate([case_diameter * 0.4, 0, case_height])
        rotate([90, 0, 0])
        hinge();
}

// Case bottom with powder reservoir
module case_bottom() {
    difference() {
        // Outer shell
        color(case_color)
        cylinder(h = case_height, r = case_diameter/2);

        // Inner cavity for powder
        translate([0, 0, wall_thickness])
            cylinder(h = case_height - wall_thickness - 1, r = case_diameter/2 - wall_thickness);

        // Decorative ring groove
        translate([0, 0, case_height - 2])
            cylinder(h = 1, r = case_diameter/2 - 3);
    }

    // Internal powder surface
    translate([0, 0, wall_thickness + 1])
        color("Beige")
        cylinder(h = 0.5, r = case_diameter/2 - wall_thickness - 1);
}

// Case top with mirror
module case_top() {
    difference() {
        // Outer shell
        color(case_color)
        cylinder(h = case_height, r = case_diameter/2);

        // Mirror recess
        translate([0, 0, wall_thickness])
            cylinder(h = case_height - wall_thickness - 2, r = case_diameter/2 - wall_thickness);
    }

    // Mirror
    translate([0, 0, wall_thickness + 0.5])
        color(mirror_color)
        cylinder(h = 0.5, r = case_diameter/2 - wall_thickness - 1);

    // Closure latch
    translate([case_diameter/2 - 3, 0, case_height - 3])
        cube([3, 6, 3], center = true);
}

// Soft puff applicator
module puff() {
    // Main puff body
    color(puff_color)
    translate([0, 0, 0])
        cylinder(h = puff_height, r = puff_diameter/2);

    // Ribbed texture lines
    for (i = [1:4]) {
        rotate([0, 0, i * 45])
            translate([puff_diameter/4, 0, puff_height/2])
                scale([1, 0.1, 1])
                    sphere(r = puff_height/2);
    }

    // Handle grip
    translate([0, 0, puff_height])
        cylinder(h = 8, r1 = 8, r2 = 6);
}

// Hinge mechanism
module hinge() {
    color(hinge_color) {
        // Main hinge pin
        cylinder(h = case_diameter/4, r = 2);

        // Hinge knuckles
        for (i = [-1, 1]) {
            translate([0, i * 5, 0])
                cylinder(h = 4, r = 3);
        }
    }
}

// Assemble the complete model
rotate([30, 0, 0])
    powder_compact();

