// Pellet tool (pellet sizer & cutter) – realistic, medium complexity
// Overall length ≈ 90 mm, printed lying flat (no overhangs)

// Color scheme
pelletColor = "silver";
handleColor = "darkolivegreen";
bladeColor = "silver";
markColor  = "black";

// ---------- PARAMETERS ----------
pelletD  = 5.5;      // .22 cal pellet diameter
pelletL  = 10;       // pellet length
handleL  = 65;
handleD  = 20;
cutterL  = 15;
cutterD  = 8;
bladeT   = 0.6;      // cutting blade thickness
gripR    = 1.0;      // grip ring radius
gripStep = 4;        // spacing between grip rings
wall     = 2.0;      // minimum wall thickness

// ---------- MODULES ----------

// Pellet sizing die (cavity)
module pelletDie() {
    difference() {
        color(pelletColor) cylinder(h=pelletL, d=pelletD+2*wall, $fn=64);
        translate([0,0,-1]) cylinder(h=pelletL+2, d=pelletD, $fn=64);
    }
}

// Handle with grip rings
module handle() {
    color(handleColor) {
        difference() {
            // Main handle body
            cylinder(h=handleL, d=handleD, $fn=64);
            // Center bore for pellet path
            translate([0,0,-1]) cylinder(h=handleL+2, d=pelletD+2*wall, $fn=64);
        }
        // Grip rings
        for(i=[0:ceil(handleL/gripStep)]) {
            translate([0,0,i*gripStep]) {
                difference() {
                    cylinder(h=gripR*2, d=handleD+2*gripR, $fn=64);
                    cylinder(h=gripR*2+0.1, d=handleD, $fn=64);
                }
            }
        }
    }
}

// Cutting head with blade
module cutterHead() {
    color(bladeColor) {
        difference() {
            cylinder(h=cutterL, d=cutterD, $fn=64);
            // Blade slot
            translate([0,0,cutterL/2-bladeT/2])
                cube([cutterD+2, 0.2, bladeT+0.1], center=true);
            // Pellet exit hole
            translate([0,0,-1]) cylinder(h=cutterL+2, d=pelletD*1.2, $fn=64);
        }
        // Actual blade insert
        translate([0,0,cutterL/2-bladeT/2])
            rotate([0,90,0]) cube([cutterD*0.9, cutterD*0.9, bladeT], center=true);
    }
}

// Markings
module markings() {
    color(markColor) {
        // Size label
        translate([handleD/2+0.2,0,handleL/2])
            rotate([0,90,0])
            linear_extrude(height=0.5) text(".22", size=3, halign="center");
    }
}

// ---------- ASSEMBLY ----------
handle();
translate([0,0,handleL]) union() {
    pelletDie();
    translate([0,0,pelletL]) cutterHead();
}
markings();

