// Brick Trowel - Realistic style, medium complexity
// Overall length ~180mm, blade width ~100mm

// Main colors
blade_color = "#C0C0C0";
handle_color = "#8B4513";
grip_color = "#2F4F4F";

// Basic dimensions
blade_length = 120;
blade_width = 100;
blade_thickness = 2;
tang_length = 40;
handle_length = 80;
handle_diameter = 30;

// Create the blade
module blade() {
    color(blade_color) {
        // Main blade shape - tapered rectangle with rounded front
        linear_extrude(height=blade_thickness, convexity=10) {
            polygon(points=[
                [0, 0],  // Handle end
                [0, blade_width],  // Handle end
                [blade_length-20, blade_width-10],  // Tapered sides
                [blade_length, blade_width/2],  // Rounded tip
                [blade_length-20, 10]  // Tapered sides
            ]);
        }

        // Reinforcement ridge along blade center
        translate([blade_length/2, blade_width/2, 0])
            cube([blade_length-20, 5, 3], center=true);

        // Tang connecting to handle
        translate([-tang_length/2, blade_width/2, 0])
            cube([tang_length, 15, 10], center=true);
    }
}

// Create the handle
module handle() {
    color(handle_color) {
        // Main handle body - slightly tapered cylinder
        translate([-tang_length - handle_length/2, blade_width/2, 0])
            rotate([0, 90, 0])
                cylinder(h=handle_length, r1=handle_diameter/2, r2=handle_diameter/2 - 3, $fn=32);

        // Handle ferrule (metal ring)
        color("#B87333")
            translate([-tang_length - 5, blade_width/2, 0])
                rotate([0, 90, 0])
                    cylinder(h=10, r=handle_diameter/2 + 2, $fn=32);
    }

    // Grip texture - raised rings
    color(grip_color) {
        for(i=[1:4]) {
            translate([-tang_length - i*18 - 10, blade_width/2, 0])
                rotate([0, 90, 0])
                    cylinder(h=5, r=handle_diameter/2, $fn=32);
        }
    }
}

// Create the curved neck transition between blade and handle
module neck_transition() {
    color(blade_color) {
        translate([-5, blade_width/2, 0])
            rotate([0, 90, 0])
                cylinder(h=10, r1=8, r2=12, $fn=32);
    }
}

// Create the blade edge detail
module blade_edge() {
    color("#A0A0A0") {
        // Thinner cutting edge
        translate([0, blade_width/2, -1])
            linear_extrude(height=1, convexity=10) {
                polygon(points=[
                    [0, 0],
                    [0, blade_width],
                    [blade_length-15, blade_width-5],
                    [blade_length-5, blade_width/2],
                    [blade_length-15, 5]
                ]);
            }
    }
}

// Add some surface details
module surface_details() {
    // Manufacturer's mark on blade
    color("#808080")
        translate([blade_length/2, blade_width/2, blade_thickness+0.1])
            rotate([0, 0, 15])
                cube([20, 3, 0.5], center=true);

    // Handle end cap
    color("#B87333")
        translate([-tang_length - handle_length - 5, blade_width/2, 0])
            rotate([0, 90, 0])
                sphere(r=handle_diameter/2, $fn=32);
}

// Assembly
union() {
    blade();
    handle();
    neck_transition();
    blade_edge();
    surface_details();
}

