// Realistic Slate Tool - Medium Complexity
// A traditional roofing slate with hole and beveled edges

// Color definitions
slate_color = [0.25, 0.26, 0.28];      // Dark slate gray
hole_color = [0.15, 0.15, 0.15];       // Darker hole interior

// Main parameters
slate_length = 80;
slate_width = 50;
slate_thickness = 4;
hole_diameter = 6;
hole_offset = 8;
bevel_angle = 15;

// Create realistic slate with beveled edges
module slate_body() {
    difference() {
        // Main slate body with beveled top
        union() {
            // Bottom flat part
            translate([0, 0, slate_thickness/2])
                cube([slate_length, slate_width, slate_thickness], center=true);

            // Beveled top edges
            for(x = [-slate_length/2 + 2, slate_length/2 - 2]) {
                translate([x, 0, slate_thickness + 1])
                    rotate([0, 0, x > 0 ? 0 : 180])
                        linear_extrude(height=2)
                            polygon(points=[
                                [0, -slate_width/2],
                                [2, -slate_width/2 + 2],
                                [2, slate_width/2 - 2],
                                [0, slate_width/2]
                            ]);
            }

            // Side bevels
            for(y = [-slate_width/2 + 2, slate_width/2 - 2]) {
                translate([0, y, slate_thickness + 1])
                    rotate([0, 0, y > 0 ? 90 : -90])
                        linear_extrude(height=2)
                            polygon(points=[
                                [0, -slate_length/2],
                                [2, -slate_length/2 + 2],
                                [2, slate_length/2 - 2],
                                [0, slate_length/2]
                            ]);
            }
        }

        // Mounting hole
        translate([slate_length/2 - hole_offset, 0, 0])
            cylinder(h=slate_thickness + 2, d=hole_diameter, $fn=32);

        // Countersink for screw head
        translate([slate_length/2 - hole_offset, 0, slate_thickness/2 + 1])
            cylinder(h=2, d1=hole_diameter * 1.8, d2=hole_diameter, $fn=32);

        // Natural slate texture - small chips
        for(i = [1:8]) {
            rotate([0, 0, i * 45])
                translate([
                    (i % 2 == 0 ? 1 : -1) * (5 + i * 3),
                    (i % 3 == 0 ? 1 : -1) * (3 + i * 2),
                    slate_thickness
                ])
                    sphere(d=1.5 + (i % 3), $fn=8);
        }
    }
}

// Natural slate surface texture
module slate_texture() {
    // Random small surface variations
    for(i = [1:20]) {
        translate([
            (rand(i) - 0.5) * slate_length * 0.8,
            (rand(i * 2) - 0.5) * slate_width * 0.8,
            slate_thickness + 0.1
        ])
            sphere(d=0.5 + rand(i * 3) * 0.5, $fn=6);
    }
}

// Helper function for pseudo-random values
function rand(n) = (n * 9301 + 49297) % 233280 / 233280;

// Create the complete slate
color(slate_color) {
    union() {
        slate_body();

        // Add subtle texture
        color([0.2, 0.21, 0.23]) {
            slate_texture();
        }
    }
}

// Add the hole interior in darker color
color(hole_color) {
    translate([slate_length/2 - hole_offset, 0, -slate_thickness/2])
