    difference() {
        // main grip body
        hull() {
            translate([-5, -8, 0]) cube([10, 16, 35]);
            translate([-7, -10, 30]) cube([14, 20, 10]);
        }

        // finger grooves
        for (z = [5:9:35])
            translate([0, 0, z])
                rotate([90, 0, 0])
                    cylinder(h = 12, d = 5, center = true);

        // hanging hole
