// Hoop frame for net
module hoop() {
    color(color_hoop) {
        // Main hoop ring
        rotate_extrude(angle=360) {
            translate([hoop_outer/2, 0, 0]) {
                circle(d=hoop_thickness);
            }
        }

        // Support arms
        for(angle = [0:120:240]) {
            rotate([0, 0, angle]) {
                translate([hoop_outer/4, 0, -hoop_thickness/2]) {
                    cube([hoop_outer/2, hoop_thickness, hoop_thickness]);
                }
            }
        }
    }
}

// Net mesh
module net() {
    color(color_net) {
        // Net bottom
        translate([0, 0, -net_depth]) {
            cylinder(h=1, d=hoop_outer-10);
        }

        // Net sides - simplified mesh pattern
        for(i = [0:15:360]) {
            rotate([0, 0, i]) {
                // Vertical strands
                for(j = [0:5:net_depth]) {
                    translate([(hoop_outer-10)/2-1, 0, -j]) {
                        rotate([90, 0, 0]) {
                            cylinder(h=2, d=1.5);
