                    for(j = [0:8:net_depth]) {
                        translate([(hoop_outer-10)/2-1, 0, -j]) {
                            rotate([90, 0, 0]) {
                                cylinder(h=2, d=1);
                            }
                        }
                    }
                }
            }
        }
    }
}

