    difference() {
        cylinder(h=cap_height, d=cap_diameter, center=true);
        cylinder(h=cap_height+0.1, d=cap_diameter-wall_thickness*2, center=true);
    }

    // Top rim reinforcement
    translate([0, 0, cap_height/2 - wall_thickness/2])
    cylinder(h=wall_thickness, d=cap_diameter+2, center=true);

    // Bottom rim reinforcement
    translate([0, 0, -cap_height/2 + wall_thickness/2])
    cylinder(h=wall_thickness, d=cap_diameter+2, center=true);

    // Grip teeth inside cap - angled for bottle cap grip
    for(i = [0:11]) {
        rotate([0, 0, i*30])
        translate([cap_diameter/2 - wall_thickness - 2, 0, 0])
        rotate([0, 15, 0])
        cube([4, 2, cap_height - 4], center=true);
    }

    // Handle attachment point
    translate([0, cap_diameter/2, 0])
    rotate([90, 0, 0])
    difference() {
        cylinder(h=10, d=handle_diameter, center=true);
        cylinder(h=11, d=handle_diameter-wall_thickness*2, center=true);
    }
}

module handle() {
