// Realistic Washer Tool – OpenSCAD
// Medium complexity, 3D-printable, no overhangs

$fn = 36;

/* ---------- CONFIGURATION ---------- */
body_d   = 70;          // overall diameter
body_h   = 15;          // overall thickness
shaft_d  = 12;          // shaft hole
grip_d   = 30;          // grip ring diameter
grip_h   = 8;           // grip ring thickness
grip_r   = 2;           // grip ring fillet radius
knurl_n  = 60;          // number of knurls around grip
knurl_w  = 1.2;         // knurl width
knurl_h  = 0.8;         // knurl height
washer_d = 22;          // washer seat diameter
washer_t = 2;           // washer seat thickness
slot_w   = 8;           // slot width for washer removal
slot_h   = 2;           // slot depth
color_tool   = [0.7,0.7,0.75];  // aluminium
color_grip   = [0.1,0.1,0.1];   // rubber
color_insert = [0.9,0.9,0.9];   // washer seat

/* ---------- MODULES ---------- */
module main_body() {
    difference() {
        cylinder(d=body_d, h=body_h, center=true);
        // shaft hole
        translate([0,0,-body_h/2-1])
            cylinder(d=shaft_d, h=body_h+2, center=false);
        // slot for washer removal
        translate([0, -body_d/2, 0])
            rotate([90,0,0])
            cube([slot_w, body_h+2, body_d], center=true);
    }
}

module grip_ring() {
    difference() {
        // main ring
        cylinder(d=grip_d, h=grip_h, center=true);
        // inner hole to fit body
        translate([0,0,-grip_h/2-1])
            cylinder(d=body_d-4, h=grip_h+2, center=false);
    }
}

module knurls() {
    // knurls around grip ring
    for(i=[0:knurl_n-1]) {
        rotate([0,0,i*360/knurl_n])
        translate([grip_d/2, 0, 0])
            cube([knurl_w, grip_h, knurl_h], center=true);
    }
}

module washer_seat() {
    difference() {
        // washer pocket
        cylinder(d=washer_d, h=washer_t, center=true);
        // shaft hole continues
        translate([0,0,-washer_t/2-1])
            cylinder(d=shaft_d, h=washer_t+2, center=false);
    }
}

/* ---------- ASSEMBLY ---------- */
union() {
    color(color_tool)   main_body();
    color(color_insert) translate([0,0,body_h/2-washer_t/2]) washer_seat();
    color(color_grip) {
        grip_ring();
        translate([0,0,grip_h/2+0.1]) knurls();
        translate([0,0,-grip_h/2-0.1]) mirror([0,0,1]) knurls();
    }
}

