// Loop Tool - Realistic Style, Medium Complexity
// A sturdy 3D printable loop tool with ergonomic handle and reinforced blade

$fn = 64; // Smooth curves

// Main dimensions
handle_length = 85;
handle_diameter = 18;
loop_inner_diameter = 35;
loop_outer_diameter = 42;
blade_thickness = 2.5;
transition_length = 12;

// Colors
color_handle = [0.1, 0.1, 0.1]; // Dark gray/black
color_loop = [0.7, 0.7, 0.7]; // Metallic gray
color_grip = [0.2, 0.2, 0.2]; // Darker grip texture

module loop_tool() {
    // Handle with ergonomic grip
    color(color_handle) {
        // Main handle body
        translate([0, 0, handle_length/2]) {
            cylinder(h=handle_length, d=handle_diameter, center=true);
        }

        // Grip texture rings
        for(i = [10:10:handle_length-10]) {
            color(color_grip) {
                translate([0, 0, i]) {
                    difference() {
                        cylinder(h=3, d=handle_diameter+1, center=true);
                        cylinder(h=3.5, d=handle_diameter-2, center=true);
                    }
                }
            }
        }
    }

    // Transition from handle to loop
    color(color_loop) {
        translate([0, 0, handle_length]) {
            // Tapered transition
            cylinder(h=transition_length, d1=handle_diameter, d2=loop_outer_diameter);

            // Reinforcement ring
            translate([0, 0, transition_length/2]) {
                cylinder(h=transition_length+2, d=loop_outer_diameter+6, center=true);
            }
        }
    }

    // Main loop blade
    color(color_loop) {
        translate([0, 0, handle_length + transition_length + loop_outer_diameter/2]) {
            difference() {
                // Outer loop
                cylinder(h=blade_thickness, d=loop_outer_diameter, center=true);

                // Inner hole
                cylinder(h=blade_thickness+1, d=loop_inner_diameter, center=true);
            }

            // Blade edge bevel
            difference() {
                cylinder(h=blade_thickness, d=loop_outer_diameter, center=true);
                cylinder(h=blade_thickness+0.5, d=loop_outer_diameter-3, center=true);
            }
        }
    }

    // Reinforcement struts
    color(color_loop) {
        for(angle = [0:120:240]) {
            rotate([0, 0, angle]) {
                translate([loop_outer_diameter/2 + 5, 0, handle_length + transition_length/2]) {
                    rotate([90, 0, 0]) {
                        cylinder(h=loop_outer_diameter/2 + 5, d=4);
                    }
                }
            }
        }
    }

    // Handle end cap
    color(color_handle) {
        translate([0, 0, 5]) {
            sphere(d=handle_diameter);
        }
    }

    // Hanging hole
    difference() {
        loop_tool();
        translate([0, 0, 5]) {
            cylinder(h=10, d=6);
        }
    }
}

loop_tool();

