// Handle with ergonomic grip
module handle() {
    difference() {
        union() {
            // Main handle cylinder
            cylinder(h = handle_length, d = handle_diameter, center = true);

            // Grip rings
            for (i = [-handle_length/2 + 5 : 7 : handle_length/2 - 10]) {
                translate([0, 0, i])
                cylinder(h = 2, d = handle_diameter + 2, center = true);
            }
        }

        // Grip texture holes
        for (i = [-handle_length/2 + 3 : 5 : handle_length/2 - 3]) {
            rotate([0, 90, 0])
            translate([0, 0, i])
            cylinder(h = 1, d = 2, center = true);
        }
    }
