// Wrench head with fixed jaw
module wrench_head() {
    difference() {
        union() {
            // Main head body
            cube([head_width, head_thickness, head_width], center = true);

            // Fixed jaw
            translate([head_width/2 - jaw_thickness, 0, jaw_max_open/2])
            cube([jaw_thickness, head_thickness, jaw_max_open], center = true);

            // Reinforcement ridges
            for (i = [-head_width/4, head_width/4]) {
                translate([i, 0, -head_width/2 - 5])
                cube([2, head_thickness, 5], center = true);
            }
        }

        // Movable jaw slot
        translate([-head_width/2, 0, -jaw_max_open/2 - 2])
        cube([head_width/2, head_thickness + 1, jaw_max_open + 4], center = true);

        // Screw hole
        rotate([90, 0, 0])
        translate([0, -head_width/2 - 5, 0])
        cylinder(h = head_width + 10, d = 3, center = true);
    }
}

// Adjustment screw mechanism
module adjustment_screw() {
    // Screw threads
    for (i = [-5 : 1 : 5]) {
        rotate([0, 0, i * 30])
        translate([0, -head_width/2 - 5, 0])
        cylinder(h = 1, d1 = 3, d2 = 2.8, center = true);
    }

    // Screw head
    translate([0, -head_width/2 - 8, 0])
    cylinder(h = 3, d = 5, center = true);

    // Screw slot
    translate([0, -head_width/2 - 8, 0])
    rotate([90, 0, 0])
    cube([3, 0.5, 5], center = true);
}

