// Main module
module grip_tool() {
    // Position the entire tool
    rotate([0, 0, 45]) {
        union() {
            // Left handle assembly
            translate([-25, 0, 0]) {
                color(color_handle) handle_assembly();
            }

            // Right handle assembly
            mirror([1, 0, 0]) {
                color(color_handle) handle_assembly();
            }

            // Jaw mechanism
            color(color_metal) jaw_mechanism();

            // Adjustment screw
            translate([0, 0, 8]) {
                color(color_screw) adjustment_screw();
            }

