// Handle assembly module
module handle_assembly() {
    // Main handle body
    difference() {
        // Handle grip
        rotate([0, 90, 0]) {
            cylinder(h=60, r1=6, r2=5);
        }

        // Grip pattern grooves
        for(i = [-20:10:20]) {
            translate([i, 0, -6]) {
                rotate([0, 90, 0]) {
                    cylinder(h=1, r=5.5);
                }
            }
