        color(color_grip) {
            rotate([0, 90, 0]) {
                cylinder(h=50, r=5.5, r2=4.5);
            }
        }

        // Textured grip pattern
        for(i = [-20:5:20]) {
            for(j = [-2:2:2]) {
                translate([i, j, 0]) {
                    rotate([0, 90, 0]) {
                        cylinder(h=0.5, r=0.5);
