            cube([30, 8, 15]);

            // Grip teeth
            for(i = [0:2:12]) {
                translate([i, 8, 0]) {
                    cube([2, 1, 15]);
                }
            }

            // Jaw curve
            translate([15, 0, 0]) {
                rotate([0, 0, 90]) {
                    cylinder(h=30, r=8);
                }
            }
        }
    }

    // Jaw pivot
    translate([0, 0, -7.5]) {
