// Create the complete knob assembly
module knob_tool() {
    // Main knob body
    color(color_base)
    difference() {
        // Outer body with slight taper
        cylinder(h=knob_height, d1=knob_diameter, d2=knob_diameter*0.95, $fn=64);

        // Top chamfer
        translate([0, 0, knob_height-3])
            cylinder(h=3, d1=knob_diameter*0.9, d2=knob_diameter, $fn=64);

        // Mounting hole
        translate([0, 0, -1])
            cylinder(h=mounting_hole_depth+1, d=mounting_hole_diameter, $fn=32);
    }

    // Grip ridges around circumference
    color(color_grip)
    for(i = [0:num_ridges-1]) {
        rotate([0, 0, i*360/num_ridges])
            translate([knob_diameter/2 - ridge_width/2 - 1, 0, knob_height-grip_height])
                cube([ridge_width, ridge_height*2, grip_height], center=true);
    }

    // Top surface grip pattern
    color(color_grip)
    difference() {
        // Raised center disc
        translate([0, 0, knob_height])
            cylinder(h=2, d=knob_diameter*0.6, $fn=64);

        // Concentric rings for grip
        for(r = [knob_diameter*0.15 : knob_diameter*0.08 : knob_diameter*0.55]) {
            translate([0, 0, knob_height+1.5])
                cylinder(h=1, d=r, $fn=64);
        }
    }

    // Shaft/collar below knob
    color(color_highlight)
    translate([0, 0, -shaft_length])
        cylinder(h=shaft_length, d=shaft_diameter, $fn=32);

    // Shaft keyway (D-cut)
    color(color_highlight)
    translate([0, -shaft_diameter/2, -shaft_length])
        cube([shaft_diameter, shaft_diameter/2, shaft_length]);

    // Reinforcement ribs
    color(color_base)
    for(angle = [0, 120, 240]) {
        rotate([0, 0, angle])
            translate([0, shaft_diameter/2, -shaft_length/2])
                cube([4, shaft_diameter/2+2, shaft_length], center=true);
    }

    // Index mark on top
    color([1, 0, 0])  // Red indicator
    translate([0, knob_diameter*0.35, knob_height+2.5])
        cube([3, 8, 1], center=true);
}

