// Main assembly
module jet_tool() {
    // Main body cylinder
    color(color_body)
    cylinder(h = body_length, d = body_diameter, center = false);

    // Fuel tank section
    color(color_blue)
    translate([0, 0, body_length - tank_length])
    cylinder(h = tank_length + 2, d = tank_diameter, center = false);

    // Fuel level window
    color(color_clear)
    translate([tank_diameter/2 + 0.5, 0, body_length - tank_length/2])
    cube([1, window_width, window_height], center = true);

    // Nozzle assembly
    color(color_metal)
    translate([0, 0, body_length])
    cylinder(h = nozzle_length, d = nozzle_diameter, center = false);

