    color(color_metal)
    translate([0, 0, body_length + nozzle_length - 2])
    cylinder(h = 8, d1 = nozzle_diameter + 2, d2 = nozzle_diameter + 6, center = false);

    // Trigger mechanism
    color(color_red)
    translate([body_diameter/2 + 2, 0, body_length/2])
    rotate([0, 90, 0])
    cylinder(h = trigger_width, d = trigger_height, center = true);

