    translate([-body_diameter/2 - 1, 0, body_length - 25])
    cube([safety_width, safety_height, 4], center = true);

    // Refill valve
    color(color_metal)
    translate([0, 0, 2])
    cylinder(h = 4, d = 6, center = false);

