        translate([0, 0, -1])
            cylinder(h=tube_length+2, d=tube_diameter-2*wall_thickness, $fn=64);
    }
}

module external_threads(start_z, direction=1) {
    // Create helical threads on outside
    for (i = [0:thread_segments-1]) {
        angle = i * 360 / thread_segments;
        translate([0, 0, start_z + direction * i * thread_pitch / thread_segments]) {
            rotate([0, 0, angle]) {
                translate([tube_diameter/2, 0, 0]) {
                    cube([thread_depth, thread_pitch/2, thread_pitch/2], center=true);
                }
            }
        }
    }
}

module hex_flats() {
    // Top hexagonal nut
    translate([0, 0, tube_length - hex_height/2])
        cylinder(h=hex_height, d=hex_size, $fn=6);

    // Bottom hexagonal nut
    translate([0, 0, hex_height/2])
        cylinder(h=hex_height, d=hex_size, $fn=6);
}

module grip_texture() {
    // Add grip ridges between hex flats
    for (i = [0:grip_ridges-1]) {
        rotate([0, 0, i * 360 / grip_ridges]) {
            translate([tube_diameter/2 - grip_depth/2, 0, tube_length/2]) {
                cube([grip_depth, grip_width, tube_length - 2*hex_height - 4], center=true);
            }
        }
    }
}

module chamfer_edges() {
    // Top chamfer
    translate([0, 0, tube_length-1])
        cylinder(h=2, d1=tube_diameter, d2=tube_diameter-2, $fn=64);

    // Bottom chamfer
    translate([0, 0, 1])
        cylinder(h=2, d1=tube_diameter-2, d2=tube_diameter, $fn=64);
}

// Main assembly
color("silver") union() {
    tube_body();

    // Add hexagonal flats
    color("gray40") hex_flats();

    // Add grip texture
    color("gray60") grip_texture();

    // Add chamfers for printability
    color("silver") chamfer_edges();

    // Add external threads
    color("gray50") {
        external_threads(hex_height, 1);
        external_threads(tube_length - hex_height - thread_pitch*3, -1);
    }
}

