mount_color = "gray";
handle_color = "darkslategray";

// Prism module - triangular prism
module triangular_prism(height, base_width) {
    // Create the triangular cross-section using difference
    linear_extrude(height = height, center = true) {
        difference() {
            // Main square to cut from
            square([base_width, base_width], center = true);
            // Cut out the triangle
            translate([-base_width/2, -base_width/2, 0])
                polygon(points=[[0,0], [base_width,0], [base_width/2,base_width/2]]);
        }
    }
}

// Prism mount with adjustment mechanism
module prism_mount(size, thickness) {
    difference() {
        // Main mount base
        cube([size + thickness*2, size + thickness*2, thickness], center = true);

        // Cutout for prism
        translate([0, 0, -thickness/2])
            triangular_prism(size + 2, size + 2);

        // Mounting holes
        for(i = [-1, 1]) {
            translate([i*(size/2 + thickness/2), 0, 0])
                rotate([0, 90, 0])
                    cylinder(h = thickness*2, d = 4, center = true);
        }
    }
}

// Handle with grip texture
module handle(length, diameter) {
    difference() {
        // Main handle cylinder
        color(handle_color)
            cylinder(h = length, d = diameter, center = true);

        // Grip grooves
        for(i = [1:5:length-5]) {
            translate([0, 0, -length/2 + i])
                cylinder(h = 2, d = diameter + 2, center = true);
        }
    }

    // End caps
    translate([0, 0, -length/2 - 1])
        cylinder(h = 2, d = diameter + 4, center = true);
    translate([0, 0, length/2 + 1])
        cylinder(h = 2, d = diameter + 4, center = true);
}

// Adjustment wheel
module adjustment_wheel(radius, thickness) {
    difference() {
        cylinder(h = thickness, d = radius * 2, center = true);

        // Knurling pattern
        for(i = [0:15:360]) {
            rotate([0, 0, i])
                translate([radius - 1, 0, 0])
                    cube([2, 1, thickness + 0.1], center = true);
        }

        // Center hole
        cylinder(h = thickness + 0.1, d = 3, center = true);
    }
}

// Assemble the prism tool
union() {
    // Prism
    color(prism_color)
        translate([0, 0, mount_thickness/2 + prism_size/2])
            rotate([0, 90, 0])
                triangular_prism(prism_size, prism_size);

    // Mount
    color(mount_color)
        translate([0, 0, 0])
            prism_mount(prism_size, mount_thickness);

    // Handle
    translate([0, -prism_size/2 - handle_diameter/2 - 5, 0])
        rotate([90, 0, 0])
            handle(handle_length, handle_diameter);

    // Adjustment mechanism
    translate([prism_size/2 + mount_thickness + 10, 0, 0])
        rotate([0, 90, 0])
            adjustment_wheel(8, 4);

    // Support struts
    for(i = [-1, 1]) {
        color(mount_color)
            translate([i*(prism_size/4), -prism_size/4 - handle_diameter/2 - 2.5, 0])
                cube([3, 5, mount_thickness], center = true);
    }

    // Scale markings
    for(i = [0:4]) {
        color("black")
            translate([prism_size/2 + mount_thickness + 15, 0, -mount_thickness/2 + i*mount_thickness/4])
                cube([0.5, 2, 0.2], center = true);
    }
}

